    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        Produto produto = con.getProdutoById(searchField.getText());
        idF.setText(produto.getId());
        nomeF.setText(produto.getNome());
        qntdF.setText("" + produto.getQntd());
        precoF.setText("" + produto.getPreco());
    }//GEN-LAST:event_jButton3ActionPerformed

